import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import helper from './public/helper';
import { BrowserRouter as Router, Route, NavLink, Link, Switch } from 'react-router-dom';
import LayoutNavBottom from './layout/nav/bottom';
import { Style, Lang } from './public/resource';
/** 引用模块 **/
import ModuleLayout from './layout';
import ModuleCommonUI from './commonUI';
import ModuleDevice from './device';
import ModuleOther from './other';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
	
  render() {
  	let {location, history} = this.props;
  	let props = {location, history};
		let myLocation = Object.assign({}, location);

    return (
      <div className="App">
      	<LayoutNavBottom {...props} navKeep contentKeep
	      	navStyle={{background: "rgba(0,0,0,.88)"}}
	      	navLinkStyle={{color: "#fff", margin:"10px auto"}}
        	navTpl='(item) => `<span class="title">${item.title}</span>`'>
        	<Route replace exact path="/layout" component={() => <ModuleLayout {...props} />} title={Lang.nav[0]} />
        	<Route replace strict path="/commonUI" component={() => <ModuleCommonUI {...props} />} title={Lang.nav[1]} />
        	<Route replace strict path="/device" component={() => <ModuleDevice {...props} />} title={Lang.nav[2]} />
        	<Route replace exact path="/other" component={() => <ModuleOther {...props} />} title={Lang.nav[3]} />
      	</LayoutNavBottom>
      </div>
    );
  }
}

export default App;
