import React, { Component } from 'react';
import PropTypes from 'prop-types';
import LayoutSideBase from './base';

export default class LayoutSideBottom extends LayoutSideBase {
  constructor(props) {
  	super(props, {
  		panelStyle: Object.assign({
  			bottom: "0",
  			left: "0",
	  		background: "rgba(0,0,0,.88)",
	  		width: "100%"
  		}, props.panelStyle)
  	});
  	
    this.direction = {
    	"PUSH": "bottom",
    	"POP": "bottom"
    };
	}
}

LayoutSideBottom.propTypes = {
	
}