import React, { Component } from 'react';
import PropTypes from 'prop-types';
import LayoutSideBase from './base';

export default class LayoutSideRight extends LayoutSideBase {
  constructor(props) {
  	super(props, {
  		panelStyle: Object.assign({
  			top: "0",
  			right: "0",
	  		background: "rgba(0,0,0,.88)",
	  		width: "80vw",
	  		height: "100vh"
  		}, props.panelStyle),
	  	backdropStyle: Object.assign({
	  		background: "rgba(0,0,0,.2)"
	  	}, props.backdropStyle)
  	});
  	
    this.direction = {
    	"PUSH": "right",
    	"POP": "right"
    };
	}
}

LayoutSideRight.propTypes = {
	
}